mapper.select:
SELECT count(1) AS notesSum,sum(TF) AS amountSum
from T_CPU_CONSUME t1
  WHERE NOT EXISTS (
    SELECT 1
       FROM T_CPU_CONSUME_ERROR WHERE PID=t1.PID AND PSN=t1.PSN
  ) AND SETTLE_DATE=#{date,jdbcType=VARCHAR}
    AND ZIP_FILE_NAME=#{zipFileName,jdbcType=VARCHAR}

SELECT count(1) from T_CPU_CONSUME t1
WHERE NOT EXISTS (
SELECT 1
FROM T_CPU_CONSUME_ERROR where t1.PID=PID AND t1.PSN=PSN
) AND SETTLE_DATE=#{date,jdbcType=VARCHAR}
  AND ZIP_FILE_NAME=#{zipFileName,jdbcType=VARCHAR}

SELECT count(1) from
(
  SELECT PID,PSN FROM T_CPU_CONSUME WHERE SETTLE_DATE=#{date,jdbcType=VARCHAR} and ZIP_FILE_NAME=#{zipFileName,jdbcType=VARCHAR}
  INTERSECT
  SELECT PID,PSN FROM T_CPU_CONSUME_ERROR WHERE SETTLE_DATE=#{date,jdbcType=VARCHAR} and ZIP_FILE_NAME=#{zipFileName,jdbcType=VARCHAR}
)
