mapper.select:
SELECT count(1) AS notesSum,sum(TF) AS amountSum
from T_CPU_CONSUME_NOBUS t1
  WHERE SETTLE_DATE=#{date,jdbcType=VARCHAR}
    and ZIP_FILE_NAME=#{zipFileName,jdbcType=VARCHAR}
    and NOT EXISTS (
    SELECT 1
      FROM T_CPU_CONSUME_ERROR_NOBUS WHERE PID=t1.PID AND PSN=t1.PSN and TAC=t1.TAC
  )

SELECT count(1) from T_CPU_CONSUME_NOBUS t1
WHERE SETTLE_DATE=#{date,jdbcType=VARCHAR}
and ZIP_FILE_NAME=#{zipFileName,jdbcType=VARCHAR}
and NOT EXISTS (
  SELECT 1
    FROM T_CPU_CONSUME_ERROR_NOBUS where t1.PID=PID AND t1.PSN=PSN and TAC=t1.TAC
)

SELECT count(1) from
  (
    SELECT PID,PSN,TAC FROM T_CPU_CONSUME_ERROR_NOBUS WHERE SETTLE_DATE=#{date,jdbcType=VARCHAR} and ZIP_FILE_NAME=#{zipFileName,jdbcType=VARCHAR}
    INTERSECT
    SELECT PID,PSN,TAC FROM T_CPU_CONSUME_NOBUS WHERE SETTLE_DATE=#{date,jdbcType=VARCHAR} and ZIP_FILE_NAME=#{zipFileName,jdbcType=VARCHAR}
  )
