mapper.select:
SELECT count(1) AS notesSum,sum(TF) AS amountSum
from T_MCARD_CONSUME t1
  WHERE SETTLE_DATE=#{date,jdbcType=VARCHAR}
    and ZIP_FILE_NAME=#{zipFileName,jdbcType=VARCHAR}
    and NOT EXISTS (
    SELECT 1
      FROM T_MCARD_CONSUME_ERROR where t1.PID=PID AND t1.PSN=PSN
  )

SELECT count(1) from T_MCARD_CONSUME t1
WHERE SETTLE_DATE=#{date,jdbcType=VARCHAR}
      and ZIP_FILE_NAME=#{zipFileName,jdbcType=VARCHAR}
      and NOT EXISTS (
      SELECT 1
        FROM T_MCARD_CONSUME_ERROR where t1.PID=PID AND t1.PSN=PSN
    )

SELECT count(1) from
(
  SELECT PID,PSN FROM T_MCARD_CONSUME_ERROR WHERE SETTLE_DATE=#{date,jdbcType=VARCHAR} and ZIP_FILE_NAME=#{zipFileName,jdbcType=VARCHAR}
  INTERSECT
  SELECT PID,PSN FROM T_MCARD_CONSUME WHERE SETTLE_DATE=#{date,jdbcType=VARCHAR} and ZIP_FILE_NAME=#{zipFileName,jdbcType=VARCHAR}
)
